/**
 * Mental Model Completeness Test
 *
 * Runs the repository's mental model validator and fails if any gaps are found.
 * This test intentionally ignores detailed documentation and bases checks on
 * repository artifacts plus a rough feature summary parsed from README.
 */
import { test, expect } from '@playwright/test';
import { spawnSync } from 'child_process';
import path from 'path';

function runValidator() {
  const repoRoot = path.resolve(__dirname, '../..');
  const result = spawnSync(process.execPath, ['scripts/validate-mental-model.js'], {
    cwd: repoRoot,
    encoding: 'utf-8'
  });
  return result;
}

test.describe('Project mental model completeness', () => {
  test('validator should pass without errors', async () => {
    const result = runValidator();

    // Try to parse JSON output; fallback to raw if parsing fails
    let parsed: any = null;
    try {
      parsed = JSON.parse(result.stdout || '{}');
    } catch (_) {
      // keep parsed as null
    }

    const debugOutput = () => {
      if (parsed) {
        return `\nValidator checks: ${JSON.stringify(parsed.checks, null, 2)}\n` +
               `Errors: ${JSON.stringify(parsed.errors, null, 2)}\n` +
               `Warnings: ${JSON.stringify(parsed.warnings, null, 2)}\n`;
      }
      return `\nSTDOUT: ${result.stdout}\nSTDERR: ${result.stderr}\n`;
    };

    expect(result.status, `Mental model validator failed.${debugOutput()}`).toBe(0);

    if (parsed && parsed.warnings && parsed.warnings.length > 0) {
      console.warn('[MentalModelValidator] Warnings:', parsed.warnings);
    }
  });
});

