+                    gaps.Add("Quality gate failed: docs/ directory required but missing");
+                }
+                else
+                {
+                    var docFiles = Directory.GetFiles("docs", "*.md").Length;
+                    if (docFiles < 5)
+                    {
+                        gaps.Add($"Quality gate failed: only {docFiles} documentation files found (minimum 5 required)");
